var myFont = "";
if (Graphics.prototype.setFontIntl)
  myFont = "Intl";
var animeInt;
var animateFrame = 0;
const ballIcon = atob("FhQBABAAAfAAD+AAf4AD/wAf/AD/8Af/wD//AP/6B//QH/yQP/SAP6RAfSMACR4ACPgAB+AAHwAAOA==");
const arrowIcon = atob("");
const icon =atob("");
const xUnit = g.getWidth() / 10;
const yUnit = g.getHeight() / 10;
const btnPos = [
  { x1: 3 * xUnit, y1: 5 * yUnit, x2: 6 * xUnit, y2: 9 * yUnit, x3: 4.5 * xUnit, y3: 7 * yUnit }, // AL
  { x1: 6 * xUnit, y1: 5 * yUnit, x2: 9 * xUnit, y2: 9 * yUnit, x3: 7.5 * xUnit, y3: 7 * yUnit }, // AR
  { x1: 6 * xUnit, y1: 1 * yUnit, x2: 9 * xUnit, y2: 5 * yUnit, x3: 7.5 * xUnit, y3: 3 * yUnit }, // BL
  { x1: 3 * xUnit, y1: 1 * yUnit, x2: 6 * xUnit, y2: 5 * yUnit, x3: 4.5 * xUnit, y3: 3 * yUnit }  // BR
];
const scorePos = [
  {x:1.5 *xUnit, y:6.5*yUnit}, //A score
  {x:1.5 *xUnit, y:3.5*yUnit}, //B score
];
const swapBtnPos= [
  { x1: 5 * xUnit, y1: 0 * yUnit, x2: 7 * xUnit, y2: 4 * yUnit, x3: 6 * xUnit, y3: 2 * yUnit }, // BL<->BR
  { x1: 7 * xUnit, y1: 4 * yUnit, x2: 10 * xUnit, y2: 6 * yUnit, x3: 8.5 * xUnit, y3: 5 * yUnit }, // BL<->AR
  { x1: 5 * xUnit, y1: 6 * yUnit, x2: 7 * xUnit, y2: 10 * yUnit, x3: 6 * xUnit, y3: 8 * yUnit }, // AL<->AR
  { x1: 2 * xUnit, y1: 4 * yUnit, x2: 5 * xUnit, y2: 6 * yUnit, x3: 3.5 * xUnit, y3: 5 * yUnit }  // BR
];
const swapRectPos=[
  { x1: 2 * xUnit, y1: 5 * yUnit, x2: 6 * xUnit, y2: 10 * yUnit, x3: 4 * xUnit, y3: 7.5 * yUnit }, // AL
  { x1: 6 * xUnit, y1: 5 * yUnit, x2: 10 * xUnit, y2: 10 * yUnit, x3: 8 * xUnit, y3: 7.5 * yUnit }, // AR
  { x1: 6 * xUnit, y1: 0 * yUnit, x2: 10 * xUnit, y2: 5 * yUnit, x3: 8 * xUnit, y3: 2.5 * yUnit }, // BL
  { x1: 2 * xUnit, y1: 0 * yUnit, x2: 6 * xUnit, y2: 5 * yUnit, x3: 4 * xUnit, y3: 2.5 * yUnit }  // BR
];



var configFileName = "badminton.config.json";
var playerList = [];
var playerList = require('Storage').readJSON(configFileName, true) || ["You","Guest1","Guest2","Guest3","Guest4"];
var selectedPlayer = playerList.slice(0,4);



var serviceA = true;
var stat = [];
function score(inB){
	//0 =A, 1 = B
	const lastStat = stat[stat.length - 1];
	var newStat = Object.assign({},lastStat);
	switch(inB){
		case 0 :
			newStat.B_score++;
			newStat.Servicer= 2+1- newStat.B_score%2;
			if(lastStat.Servicer>=2){
				newStat.BL = lastStat.BR;
				newStat.BR = lastStat.BL;
			}
			break;
		case 1 :
			newStat.A_score++;
			newStat.Servicer= 1- newStat.A_score%2;
			if(lastStat.Servicer<2){
				newStat.AL = lastStat.AR;
				newStat.AR = lastStat.AL;
			}
			break;
	}
	if(Math.max(newStat.A_score, newStat.B_score)<21 || (Math.max(newStat.A_score, newStat.B_score)>=21 && Math.abs(newStat.A_score-newStat.B_score)<2)){
		stat.push(newStat);
		updateFrame();
	}
	else{
		g.clear();
		animateFrame = 0;
		animeInt = setInterval(drawResult,200,newStat);
		Bangle.removeListener('touch',touchEvent);
		Bangle.on('touch',returnTouchEvent);
	}
}
function drawResult(newStat){
	var hotaru = [];
hotaru[0] = atob("");
hotaru[1] =atob("");
hotaru[2] = atob("");
hotaru[3] = atob("");
hotaru[4]= atob("");
hotaru[5] =atob("");

  g.clear();
  //g.drawImage(hotaru[frame%6],0,0,{scale:1.5});
  g.drawImage(hotaru[animateFrame%6],xUnit*2,yUnit*4.3);
  //g.setColor("#FFFFFF");
  //g.setFont("6x8:2",1);
  //g.setFontAlign(0, 0);
   g.drawString("Game",5*xUnit,1*yUnit);
    g.drawString(newStat.A_score+" VS "+newStat.B_score,5*xUnit,2*yUnit);
    g.drawString("Winner:",5*xUnit,3*yUnit);
    g.drawString(newStat.Servicer<2?newStat.AL:newStat.BL,5*xUnit,4.5*yUnit);
    g.drawString(newStat.Servicer<2?newStat.AR:newStat.BR,5*xUnit,5.5*yUnit);
  animateFrame++;
}
function updateFrame() {
  Bangle.setLCDTimeout(0); // turn off the timeout
  Bangle.setLCDPower(1); // keep screen on

  g.clear();
  const currentState = stat[stat.length - 1];
  g.setFont(myFont||"Vector:12",1);
  for (var i = 0; i < 4; i++) {
    g.setColor(i == currentState.Servicer ? "#CC0000" : "#0000CC");
    g.fillRect(btnPos[i].x1, btnPos[i].y1, btnPos[i].x2, btnPos[i].y2);
    var player = "";
    switch (i) {
      case 0: player = currentState.AL; break;
      case 1: player = currentState.AR; break;
      case 2: player = currentState.BL; break;
      case 3: player = currentState.BR; break;
    }
    g.setColor("#FFFFFF");
    g.drawString(player, btnPos[i].x3, btnPos[i].y3);
  }
  g.setColor("#00FF00");
  g.setFont(myFont||"6x8:2",1);
  g.drawString(stat[stat.length -1].A_score,scorePos[0].x,scorePos[0].y);
  g.setColor("#00FF00");
  g.setFont(myFont||"6x8:2",1);
  g.drawString(stat[stat.length -1].B_score,scorePos[1].x,scorePos[1].y);
  g.setColor("#FFFFFF"); g.drawImage(ballIcon,1*xUnit,8*yUnit,{});
  g.setColor("#FFCC00");
  g.drawCircle(xUnit*1.5,yUnit*1.5,xUnit);
  g.drawString("<",xUnit*1.5,yUnit*1.5);
}
function touchEvent (zone, e) {
  if (e.x > btnPos[0].x1 && e.x < btnPos[1].x2 && e.y > btnPos[0].y1 && e.y < btnPos[1].y2) {
    score(1);
  } else if (e.x > btnPos[3].x1 && e.x < btnPos[2].x2 && e.y > btnPos[3].y1 && e.y < btnPos[2].y2) {
    score(0);
  }
  const dx = e.x-xUnit*1.5;
  const dy = e.y-yUnit*1.5;
  if (dx * dx + dy * dy <= xUnit * xUnit && stat.length>1)
  {
    stat.pop();
    updateFrame();
  }
}
function homeTouchEvent(zone,e){
  if(e.x >xUnit * 3 && e.x<xUnit * 7&&e.y>yUnit * 8&&e.y<yUnit * 10)
  {
    Bangle.removeListener('touch',homeTouchEvent);
    g.clear();
    showMainMenu();
  }
}
function returnTouchEvent (zone, e){
  g.clear();
  clearInterval(animeInt);
  Bangle.removeListener('touch',returnTouchEvent);
  homePage();
}
function swapTouchEvent(zone,e){
  if (e.x > swapBtnPos[0].x1 && e.x < swapBtnPos[0].x2 && e.y > swapBtnPos[0].y1 && e.y < swapBtnPos[0].y2) {
    swapPlayer(2,3);
    showSwap();
  } else
    if (e.x > swapBtnPos[1].x1 && e.x < swapBtnPos[1].x2 && e.y > swapBtnPos[1].y1 && e.y < swapBtnPos[1].y2) {
    swapPlayer(1,2);
      showSwap();
  } else
    if (e.x > swapBtnPos[2].x1 && e.x < swapBtnPos[2].x2 && e.y > swapBtnPos[2].y1 && e.y < swapBtnPos[2].y2) {
    swapPlayer(0,1);
      showSwap();
  } else
    if (e.x > swapBtnPos[3].x1 && e.x < swapBtnPos[3].x2 && e.y > swapBtnPos[3].y1 && e.y < swapBtnPos[3].y2) {
    swapPlayer(0,3);
      showSwap();
  } else
    if(e.x > 0 && e.x < 2*xUnit && e.y > 2*yUnit && e.y < 8*yUnit){
      serviceA=!serviceA;
      showSwap();
    }else
    if(e.x > 0 && e.x < 2*xUnit && e.y > 0 && e.y <2*yUnit){
      Bangle.removeListener('touch',swapTouchEvent);
      showMainMenu();
    }else
    if(e.x > 0 && e.x < 2*xUnit && e.y > 8*yUnit){
      Bangle.removeListener('touch',swapTouchEvent);
      var newGame ={A_score: 0, B_score: 0, Servicer: serviceA?1:3, AL: selectedPlayer[0], AR: selectedPlayer[1], BL: selectedPlayer[2], BR: selectedPlayer[3]};
    initializeGame(newGame);
    }
}
function initializeGame(obj) {
  g.setBgColor("#000000");
  g.clear();
  g.setFontAlign(0, 0);
  stat = [];
  stat.push(obj);
  updateFrame();
  Bangle.on('touch', touchEvent);
}
function homePage(){
  Bangle.setLCDTimeout(10);
  g.setBgColor("#FFFFFF");
  g.clear();
  g.setFontAlign(0, 0);
  g.drawImage(icon,xUnit*1,yUnit*1);
  g.setColor("#FFFF00");
  const radius = 5;
const x1 = xUnit * 3;
const y1 = yUnit * 8;
const x2 = 7 * xUnit;
const y2 = 10 * yUnit;
const points = [
  x1 + radius, y1,
  x2 - radius, y1,
  x2, y1 + radius,
  x2, y2 - radius,
  x2 - radius, y2,
  x1 + radius, y2,
  x1, y2 - radius,
  x1, y1 + radius
];
g.fillPoly(points);
g.setColor("#000000");
g.setFont("4x6");
g.drawString("WiLLERCodeCode",xUnit*5,yUnit);
g.setFont(myFont||"6x8:2",1);
g.drawString("Start",xUnit*5,yUnit*9);
Bangle.on('touch', homeTouchEvent);
}
function swapPlayer(a,b){
   var temp = selectedPlayer[a];
    selectedPlayer[a] = selectedPlayer[b];
    selectedPlayer[b] = temp;
}
function showMainMenu() {
  var mainMenu={};
  mainMenu[""]= { "title" : "Select Users" };
  mainMenu["AL : " + (selectedPlayer[0] || "Empty")]=function() { showSubMenu(0);};
  mainMenu["AR : " + (selectedPlayer[1] || "Empty")]=function() { showSubMenu(1);};
  mainMenu["BL : " + (selectedPlayer[2] || "Empty")]=function() { showSubMenu(2);};
  mainMenu["BR : " + (selectedPlayer[3] || "Empty")]=function() { showSubMenu(3);};
  mainMenu.Next = function(){
    E.showMenu();
    showSwap();
    Bangle.on('touch',swapTouchEvent);
  };
  E.showMenu(mainMenu);
}
function showSwap(){
  E.showMenu();
  g.setBgColor("#000000");
  g.clear();
  g.setFont(myFont||"Vector:12",1);
  g.setFontAlign(0, 0);
  for (var i = 0; i < 4; i++) {
    var player = selectedPlayer[i];
    g.setColor(mabiColor(player));
    g.fillRect(swapRectPos[i].x1, swapRectPos[i].y1, swapRectPos[i].x2, swapRectPos[i].y2);
    g.setColor("#000000");
    g.drawString(player, swapRectPos[i].x3, swapRectPos[i].y3);
    g.drawImage(arrowIcon,xUnit*5.2,yUnit*1,{scale:0.5});
    g.drawImage(arrowIcon,xUnit*5.2,yUnit*8,{scale:0.5});
    g.drawImage(arrowIcon,xUnit*4.2,yUnit*5,{scale:0.5, rotate:3.14/2});
    g.drawImage(arrowIcon,xUnit*8,yUnit*5,{scale:0.5, rotate:3.14/2});
  }
  g.setColor("#00FFFF");
  g.setFont(myFont||"6x8",0.5);
  g.drawString("Serv.",1*xUnit,5*yUnit);
  g.setColor("#FFFFFF");
  g.drawImage(ballIcon,0.5*xUnit,(2+serviceA*4)*yUnit);
  g.setColor("#FF0000");
  g.drawCircle(xUnit*1,yUnit*1,xUnit*0.8);
  g.drawString("<",xUnit*1,yUnit*1);
  g.setColor("#00FF00");
  g.drawCircle(xUnit*1,yUnit*9,xUnit*0.8);
  g.drawString("GO", xUnit*1,yUnit*9);
}
function mabiColor(inputString){
  var intColor = [0,0,0];
  for(var i = 0; i<inputString.length;i++){
    try{
      var intChar = inputString.charCodeAt(i);
    intColor[i%3]+=intChar;
    }
    catch(error){}
  }
  	intColor[0] = (intColor[0] * 101) % 97 + 159;
		intColor[1] = (intColor[1] * 101) % 97 + 159;
		intColor[2] = (intColor[2] * 101) % 97 + 159;
  return "#"+intColor[0].toString(16)+intColor[1].toString(16)+intColor[2].toString(16);
}
function showSubMenu(index) {
  E.showMenu();
  var subMenu = {
    "" : { "title" : "Select User " + (index + 1),"back" : function() { showMainMenu(); } }
  };
  playerList.forEach(function(user) {
    subMenu[user] = function() {
      selectedPlayer[index] = user;
      showMainMenu();
    };
  });
  E.showMenu(subMenu);
}
homePage();